package strings;

import java.util.HashMap;

public class FindTheAddedChar {

	public static void main(String[] args) {
		System.out.println(findTheDifference(""
				,""));
		System.out.println(findTheDifference2(""
				,""));
	}
	//Faster solution 1
	public static char findTheDifference(String s, String t){
		char key = (char) 0;

		for(int i=0; i< s.length() ; i++){
			key = (char)(key ^ s.charAt(i) ^ t.charAt(i));
		}
		key = (char) (key ^ t.charAt(s.length()));
		return key;
	}

	//Solution 2

	public static char findTheDifference2(String s, String t){
		    HashMap<Character,Integer> letters = new HashMap<Character,Integer>();
	        char key = 'a';
	        int value = 0;
	        
	        for(int i=0; i< s.length() ; i++){
	            key = s.charAt(i);
	                value = letters.getOrDefault(key,0)+1;
	                letters.put(key, value);	            
	        }
	        
	        
	        for(int i=0; i< t.length() ; i++){
	            key = t.charAt(i);
	            if(!letters.containsKey(key)){
	                return key;
	            }
	            else{
	                value = letters.getOrDefault(key,0)-1;
	                if(value<0) break;
	                letters.put(key, value);
	            }
	        }
	        
	        return key;
		}
}
